mapper.select:
SELECT (SELECT ui.UserMidHeaderImage FROM UserInfo ui WHERE ui.userName = vkvd.user_name) userImg, (SELECT uoi.ORG_NAME_DISPLAY FROM UserInfo ui LEFT JOIN UserOrgInfo uoi on ui.UserORG_CODE=uoi.ORG_CODE WHERE ui.userName = vkvd.user_name)userOrg, (SELECT ui.UserPhoneNum FROM UserInfo ui WHERE ui.userName = vkvd.user_name) phone, (SELECT ui.UserNick FROM UserInfo ui WHERE ui.userName = vkvd.user_name) userNick, (SELECT ui.UserEmail FROM UserInfo ui WHERE ui.userName = vkvd.user_name)email,
FROM vk_viedo_details vkvd
vkvd.vaild_status=1
and vkvd.viedo_classify_id =${viedoClassifyId}
and vkvd.user_name= #{userName}


SELECT count(*)
 FROM vk_viedo_details vkvd


  vkvd.vaild_status=1 

  and  vkvd.viedo_classify_id=${viedoClassifyId}


  and  vkvd.user_name=${userName}

select count(*) from vk_viedo_details  vkvd

  vkvd.viedo_md5=#{md5}
  and vkvd.vaild_status=1;
